import {model, Schema} from 'mongoose';
import {RolesEnum} from '../rolesEnum';


export const UsersSchema = new Schema({
    name: {type: String, required: false},
    nif: {type: Number, required: false},
    phone: {type: String, required: false},
    photo: {
        type: String,
        required: false,
        default:
            'data:image/jpeg;base64,'
    },
    email: {
        type: String,
        required: true,
        index: true,
        unique: true,
    },
    address: {
        name: {
            type: String,
            required: false,
        },
        latitude: {
            type: Number,
            required: false
        },
        longitude: {
            type: Number,
            required: false
        },
    },
    deleted: {type: Boolean, required: true, default: false},
    roles: {
        type: [String],
        enum: [
            RolesEnum.ADMIN,
            RolesEnum.TEACHER,
            RolesEnum.PARENT,
            RolesEnum.NOROLE
        ],
        default: [RolesEnum.NOROLE]
    }
});


// middleware
UsersSchema.pre('save', (next) => {
    const self = this;
    if (self.noRoles && self.roles.length > 1) {
        self.roles = delete self.roles[RolesEnum.NOROLE];
    }
    next();
});

UsersSchema.methods.noRoles = () => {
    return !(this.roles) || this.roles.find(p => p === RolesEnum.NOROLE);
};

UsersSchema.methods.isAdmin = () => {
    return this.roles.find(p => p === RolesEnum.ADMIN);
};

UsersSchema.methods.isTeacher = () => {
    return this.roles.find(p => p === RolesEnum.TEACHER);
};

export const UsersModel = model(
    'Users',
    UsersSchema
);
